<script>
import userService from "@/services/userService";
export default {
  name: "StudentRegisterComponent",
  data() {
    return {
      firstName: "",
      lastName: "",
      email: "",
      address: "",
      phone: "",
      password: "",
      rePassword: "",
    };
  },
  computed: {
    passwordMatch() {
      return (
        this.password && this.rePassword && this.password !== this.rePassword
      );
    },
  },
  methods: {
    async handleSubmit() {
      try {
        if (this.passwordMatch) {
          return;
        }
        const data = {
          firstName: this.firstName,
          lastName: this.lastName,
          email: this.email,
          address: this.address,
          phone: this.phone,
          password: this.password,
        };
        const response = await userService.lecturerRegister(data);
        console.log(response);
        this.$toast.success("Register successfully!");
        this.$router.push("/login");
      } catch (error) {
        console.log(error);
        this.$toast.error("Register failed! Please try again!");
      }
    },
  },
};
</script>
